import React, { Component } from "react";
import "../styles/navbar.css";

interface NavBarProps {
    value: string;
    numTasks: number;
    numDoneTasks: number;
    onAdd: () => void;
    onClear: () => void;
    onDeleteDone: () => void;
    onReset: () => void;
    onText: (letter: string) => void;
}

class NavBar extends React.Component<NavBarProps> {
    render() {
        return (
            <nav className="navbar">
                <input
                    className="text-box"
                    placeholder="Add Task"
                    autoFocus={true}
                    type="text"
                    value={this.props.value}
                    onChange={l => this.props.onText(l.target.value)}
                    onKeyPress={k => {
                        if (k.key === "Enter") this.props.onAdd();
                    }}
                />
                <button className="add-btn" onClick={this.props.onAdd}>
                    Add
                </button>
                <button className="not-add-btn" onClick={this.props.onReset}>
                    Reset
                </button>

                <button
                    className="not-add-btn"
                    onClick={this.props.onDeleteDone}
                >
                    Delete Done
                </button>
                <button className="not-add-btn" onClick={this.props.onClear}>
                    Clear
                </button>
            </nav>
        );
    }
}

export default NavBar;
