import React, { Component } from "react";
import "../styles/task.css";

export type taskTemplate = {
    id: number;
    description: string;
    checked: boolean;
};

interface TaskProps {
    task: taskTemplate;
    onToggle: (t: taskTemplate) => void;
    onDelete: (id: number) => void;
}

class Task extends React.Component<TaskProps> {
    render() {
        return (
            <React.Fragment>
                <input
                    type="checkbox"
                    defaultChecked={this.props.task.checked}
                    onChange={() => this.props.onToggle(this.props.task)}
                    className="checkbox"
                />
                <span
                    className="task"
                    style={
                        this.props.task.checked
                            ? { textDecoration: "line-through" }
                            : {}
                    }
                >
                    {this.props.task.description}
                </span>
                <button
                    className="delete-btn"
                    onClick={() => this.props.onDelete(this.props.task.id)}
                ></button>
            </React.Fragment>
        );
    }
}

export default Task;
