import React from "react";
import "../styles/login.css";
import "../styles/popup.css";

interface LoginProps {
    show: boolean;
    //onLogin: () => void;
    //onSignUp: () => void;
}

interface LoginState {
    username: string;
    password: string;
}

class Login extends React.Component<LoginProps> {
    state: LoginState = {
        username: "",
        password: "",
    };

    handleTextUsername = (username: string): void => {
        this.setState({ username });
    };

    handleTextPassword = (password: string): void => {
        this.setState({ password });
    };

    render() {
        return this.props.show ? (
            <div className="popup-bg">
                <div className="popup-window">
                    <div className="text">
                        Please enter your credentials to either login or sign up
                    </div>
                    <input
                        className="text-box"
                        placeholder="Username"
                        autoFocus={true}
                        type="text"
                        maxLength={100}
                        value={this.state.username}
                        onChange={l => this.handleTextUsername(l.target.value)}
                    />
                    <br />
                    <input
                        className="text-box"
                        placeholder="Password"
                        type="password"
                        maxLength={100}
                        value={this.state.password}
                        onChange={l => this.handleTextPassword(l.target.value)}
                    />
                    <div className="btns">
                        <button
                            className="btn login-btn"
                            //onClick={() => this.props.onYes()}
                        >
                            Login
                        </button>
                        <button
                            className="btn"
                            //onClick={this.props.onCancel}
                        >
                            Sign-Up
                        </button>
                    </div>
                </div>
            </div>
        ) : (
            ""
        );
    }
}

export default Login;
