const crypto = require("crypto-js");

export const encode = base => {
    return crypto.AES.encrypt(base, "matanreut").toString();
    //return Buffer.from(base).toString("base64");
};

export const decode = decoded => {
    return crypto.AES.decrypt(decoded, "matanreut").toString(crypto.enc.Utf8);
    //return Buffer.from(decoded, "base64").toString("ascii");
};

export const encodeObject = base => {
    let obj = {};
    for (let [key, value] of Object.entries(base)) {
        obj[encode(key)] = encode(value);
    }
    return obj;
};

export const decodeObject = decoded => {
    let obj = {};
    for (let [key, value] of Object.entries(decoded)) {
        obj[decode(key)] = decode(value);
    }
    return obj;
};

export const encodeArray = base => {
    let arr = [];
    base.forEach(element => {
        arr.push(module.exports.encodeObject(element));
    });
    return arr;
};

export const decodeArray = decoded => {
    let arr = [];
    decoded.forEach(element => {
        arr.push(module.exports.decodeObject(element));
    });
    return arr;
};
