import React from "react";
import "./styles/App.css";
import { TaskTemplate } from "./components/task";
import Tasks from "./components/tasks";
import NavBar from "./components/navbar";

interface AppState {
    tasks: TaskTemplate[];
    currentText: string;
    popup: boolean;
    popupText: string;
}

class App extends React.Component {
    state: AppState = {
        tasks: [],
        currentText: "",
        popup: false,
        popupText: "",
    };

    maxId = (): number => {
        return this.state.tasks.reduce(
            (max, character) => (character.id > max ? character.id : max),
            0
        );
    };

    handleText = (currentText: string): void => {
        this.setState({ currentText });
    };

    handleAdd = (): void => {
        if (this.state.currentText) {
            const tasks = [...this.state.tasks];
            const newTask: TaskTemplate = {
                id: this.maxId() + 1,
                description: this.state.currentText,
                checked: false,
            };
            tasks.push(newTask);
            this.setState({ tasks: tasks, currentText: "" });
        }
    };

    handleReset = (): void => {
        this.setState({
            popup: true,
            popupText: "Are you sure you want to uncheck all tasks?",
        });
    };

    Reset = (): void => {
        const tasks = [...this.state.tasks];
        tasks.forEach(t => {
            t.checked = false;
        });
        this.setState({ tasks });
    };

    handleDeleteDone = (): void => {
        const tasks = this.state.tasks.filter(t => !t.checked);
        this.setState({ tasks });
    };

    handleClear = (): void => {
        this.setState({ tasks: [] });
    };

    handleDelete = (id: number): void => {
        const tasks = this.state.tasks.filter(t => t.id !== id);
        this.setState({ tasks });
    };

    handleToggle = (id: number): void => {
        const tasks = [...this.state.tasks];
        tasks.forEach(t => {
            if (t.id === id) t.checked = !t.checked;
        });
        this.setState({ tasks });
    };

    render() {
        return (
            <React.Fragment>
                <NavBar
                    value={this.state.currentText}
                    numTasks={this.state.tasks.length}
                    numDoneTasks={
                        this.state.tasks.filter(t => t.checked).length
                    }
                    onText={this.handleText}
                    onAdd={this.handleAdd}
                    onReset={this.handleReset}
                    onDeleteDone={this.handleDeleteDone}
                    onClear={this.handleClear}
                />
                <Tasks
                    tasks={this.state.tasks}
                    onDelete={this.handleDelete}
                    onToggle={this.handleToggle}
                />
            </React.Fragment>
        );
    }
}

export default App;
