import React from "react";
import "../styles/task.css";
import bin from "../images/cute-bin.png";
import X from "../images/X.jpg";

export type TaskTemplate = {
    id: number;
    description: string;
    checked: boolean;
};

interface TaskProps {
    task: TaskTemplate;
    onDelete: (id: number) => void;
    onToggle: (id: number) => void;
}

class Task extends React.Component<TaskProps> {
    render() {
        return (
            <div className="task">
                <div
                    className={
                        this.props.task.checked
                            ? "check-box-wrapper-checked"
                            : "check-box-wrapper"
                    }
                >
                    <input
                        type="checkbox"
                        checked={this.props.task.checked}
                        onChange={() => this.props.onToggle(this.props.task.id)}
                        className="check-box"
                    />
                    <img src={X} alt="" className="" />
                </div>
                <span
                    className="text"
                    style={
                        this.props.task.checked
                            ? { textDecoration: "line-through" }
                            : {}
                    }
                >
                    {this.props.task.description}
                </span>
                <button
                    className="delete-btn"
                    onClick={() => this.props.onDelete(this.props.task.id)}
                >
                    <img src={bin} alt="delete" className="bin" />
                </button>
            </div>
        );
    }
}

export default Task;
