import React from "react";
import "./styles/App.css";
import { taskTemplate } from "./components/task";
import Tasks from "./components/tasks";
import NavBar from "./components/navbar";

interface AppState {
    tasks: taskTemplate[];
    currentText: string;
}

class App extends React.Component<AppState> {
    state: AppState = { tasks: [], currentText: "" };
    render() {
        return (
            <React.Fragment>
                <NavBar
                    value={this.state.currentText}
                    numTasks={this.state.tasks.length}
                    numDoneTasks={
                        this.state.tasks.filter(t => t.checked).length
                    }
                    onAdd={() => {}}
                    onText={() => {}}
                    onClear={() => {}}
                    onClearDone={() => {}}
                    onReset={() => {}}
                />
                <Tasks
                    tasks={[
                        { id: 1, description: "hi", checked: false },
                        { id: 2, description: "there", checked: false },
                    ]}
                    onToggle={() => {}}
                    onDelete={() => {}}
                />
            </React.Fragment>
        );
    }
}

export default App;
